import os
import json
from bottle import Bottle, route , run , template , post , request, static_file

cles = ['Nom', 'Prenom', 'Annee', 'Cal', 'Fonct', 'Ordre','Cont','Anniv','Age',\
        'Ecole','Etude','Met','Boite']
valeurs = ['Nom de baptême', 'Nom', 'Année de baptême', 'Année de Calotte',\
           'Fonction','Ordre','Contact', 'Anniversaire', 'Age', 'Ecole',\
           "Année d'étude", "Métier", "Boîte"]

def getheader(titreonglet):
    return '''
        <! DOCTYPE html >
        <html >
        <head >
            <title >{}</title >
            <link rel = 'stylesheet' href='/style.css'>
        </head >
        '''.format(titreonglet)

app=Bottle()

#route to the style CSS
@app.route('/volcania.jpg')
def css():
    return static_file ('volcania.jpg', root='.')


@app.route('/403.png')
def css():
    return static_file ('403.png', root='.')


@app.route('/Vulcania.jpg')
def css():
    return static_file ('Vulcania.jpg', root='.')


@app.route('/style.css')
def css():
    return static_file ('style.css', root='.')


@app.route('/')
def Total():
    try:
        #Opening/Closure of the file 'base de donnés' and converting on json
        document = open('BaseDeDonnees.txt','r')                
        dico = json.load(document)                              
        document.close()
        #Name the window (Vulcania) and apply the style CSS
        textehtml = getheader('Vulcania')                       
        textehtml +='''

        <body >
        <header>
            <h1> Cercle de la Vulcania </h1>
        </header>
        
        <nav>
            <ul>
            <li>Bacchus</li>
            <li>Saint Eloy</li>
            <li>Vulcain</li>
            <li>Intégrale</li>
            </ul>
        </nav>
        
        <section>
            <a href = "http://localhost:6969/ajout" style="position: absolute;right:0px"> Ajouter </h2></a>
        </section>
        
            <p>Les personnes enregistrés :</p>
            
            <article>
            <ol>
        '''

        for nom in dico:                                                                                        
            textehtml += '''
            <li>
            <a class=article href = "http://localhost:6969/detail/{}">{}</a>
            </li>
            '''.format(nom, nom)

        textehtml += '''

        </ol>
        </article>
        
        </body>
        <footer>
            <b> Création </b></br>
            <i> Puissant Baeyens Victor, Gillyns Emmanuel </i>
        </footer>
        '''                      
    #Error handeling
    except FileNotFoundError:
        print("l'emplacement est introuvable")
    except IOError:
        print("erreur d'ouverture")
    return str(textehtml)

@app.route('/detail/<nom>')
def detail(nom):
    try:
        document = open('BaseDeDonnees.txt','r')               
        dico = json.load(document)                              
        document.close()

        #Create a dictionary with only one people from the "cercle"
        baptise = dico[nom]
        #Name the window (Nom de Baptême) and apply the style CSS
        textehtml = getheader(nom)                             
        textehtml += '''
        <body >
        <header>
            <h1>{}</h1>
        </header>
        <modif><a href = "/forbid" style="position: absolute;right:0px"> Modifier </a></modif></br>
        <detail>
        '''.format(nom)
        #We make a list with each keys:values from the people picked
        for i in baptise :                                                      
            textehtml += str(i)                                      #keys
            textehtml += "<ul><li>{}</li></ul>".format(baptise[i])   #values
                        
        textehtml += '''
        </detail>
        <p>
            <a href = "http://localhost:6969"> Retour </a>
        </p>
        <footer>
            <b> Création </b></br>
            <i> Puissant Baeyens Victor, Gillyns Emmanuel </i>
        </footer>
        </body>'''
        
    except FileNotFoundError:                                   
        print("l'emplacement est introuvable")
    except IOError:
        print("erreur d'ouverture")
    return str(textehtml)

@app.route ('/forbid')
def forbid():
    #Name the window (Forbiden) and apply the style CSS
    textehtml = getheader('Forbiden')
    textehtml += '''
    <header class="forbid">
        <h1>Vous n'avez pas l'autorisation de modifier les données!</h1>
    
    <body class="forbid">
        Veuillez contacter l'administrateur du site web. </br>
        <a href = "mailto:victor_pb@hotmail.fr" style="color: black; font-family: comic sans MS"> Lui envoyer un mail! =) </a>
    </body>
    '''
    return textehtml

            
@app.route ('/ajout')
def ajout():
    try:
        document = open('BaseDeDonnees.txt','r')
        dico = json.load(document)
        document.close()
    except FileNotFoundError:
        print("l'emplacement est introuvable")
    except IOError:
        print("erreur d'ouverture")
        
    #Name the window (Ajout) and apply the style CSS
    textehtml = getheader('Ajout')
    textehtml += '''
    <header class= 'ajout'>
        <h1> Pas encore dans le cercle??! </h1>
    </header>
    <body class="ajout">
    <form action="/succes" method="post" accept-charset="utf-8">
    '''
    for k in range (13):
        textehtml +='''
        <span class="ident">{}</span>'''.format(valeurs[k])
        
        if k == 0:
            textehtml +='''
            <span class="case">
            <input type ="text" name ={} accept-charset="utf-8" placeholder = {} />
            </span></br>'''.format(cles[k], "Fistouille")
        if k != 0:  
            textehtml +='''
            <span class="case">
            <input type ="text" name ={} accept-charset="utf-8" placeholder = {} />
            </span></br>'''.format(cles[k],dico["Fistouille"][valeurs[k]])
            
    textehtml += '''</br>
    <input type="submit" value ="Ajouter" />
    </form >
    
    <p>
    </br>
        <a href = "http://localhost:6969"> Retour </a>
    </p>
    <footer>
            <b> Création </b></br>
            <i> Puissant Baeyens Victor, Gillyns Emmanuel </i>
        </footer>
    </body>
    '''
    return textehtml

@app.post ('/succes')
def succes ():
    try:
        document = open('BaseDeDonnees.txt','r')
        dico = json.load(document)
        document.close()

        dico[request.forms.get(cles[0])]={}
        i = 1
        while i < 13:
            dico[request.forms.get(cles[0])][valeurs[i]] = request.forms.get(cles[i])
            i+=1

        #That's a duty for Super Combefis!!
        #le dictionnaire test{} est juste là pour afficher les valeurs entrées dans les champs de la page web sous forme de dictionnaire telles qu'elles seront ajoutées dans notre base de donnée
        test={}
        test[request.forms.get(cles[0])]={}
        i = 1
        while i < 13:
            test[request.forms.get(cles[0])][valeurs[i]] = request.forms.get(cles[i])
            i+=1
        print (test)

        document = open('BaseDeDonnees.txt','w')
        document.write(json.dumps(dico, sort_keys=True, indent=4, ensure_ascii=False))
        document.close()
    except FileNotFoundError:                                   #Error handeling
        print("l'emplacement est introuvable")
    except IOError:
        print("erreur d'ouverture")
    return '''
    <h1> Felicitition, vous avez entré une nouvelle personne !</h1>
    <p>
        <a href = "http://localhost:6969"> Retour </a>
    </p>
    '''

#Starting of the local server, ♫ ♫ 6,9, soixante-neuf, c'est le plaisir des dieux!! ♫ ♫ ♫ 
#run(app, host='localhost', port=6969)
run(host='0.0.0.0', port=int(os.environ.get('PORT', 5000)))

